using Firmeza.Web.Interfaces; using Firmeza.Web.Models; namespace Firmeza.Web.Services{ public class CustomerService{ private readonly ICustomerRepository _repo; private readonly IStringSanitizer _san; public CustomerService(ICustomerRepository repo,IStringSanitizer san){_repo=repo;_san=san;} public Task<List<Customer>> ListAsync(string? q=null)=>_repo.ListAsync(q); public Task<Customer?> GetAsync(Guid id)=>_repo.GetAsync(id); public async Task CreateAsync(Customer c){c.FullName=_san.Clean(c.FullName); await _repo.CreateAsync(c);} public async Task UpdateAsync(Customer c){c.FullName=_san.Clean(c.FullName); await _repo.UpdateAsync(c);} public Task DeleteAsync(Guid id)=>_repo.DeleteAsync(id);} }