using Firmeza.Web.Interfaces; using Firmeza.Web.Models; namespace Firmeza.Web.Services{ public class ProductService{ private readonly IProductRepository _repo; private readonly IStringSanitizer _san; public ProductService(IProductRepository repo,IStringSanitizer san){_repo=repo;_san=san;} public Task<List<Product>> ListAsync()=>_repo.ListAsync(); public Task<Product?> GetAsync(Guid id)=>_repo.GetAsync(id); public async Task CreateAsync(Product p){p.Name=_san.Clean(p.Name); await _repo.CreateAsync(p);} public async Task UpdateAsync(Product p){p.Name=_san.Clean(p.Name); await _repo.UpdateAsync(p);} public Task DeleteAsync(Guid id)=>_repo.DeleteAsync(id);} }